import { Component, computed, inject } from '@angular/core';

import { AuthComponent } from './auth/auth.component';
import { LearningResourcesComponent } from './learning-resources/learning-resources.component';
import { AuthService } from './auth/auth.service';
import { CommonModule } from '@angular/common';
import { AuthDirective } from './auth/auth.directive';

@Component({
  selector: 'app-root',
  standalone: true,
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  imports: [AuthComponent, LearningResourcesComponent,CommonModule,AuthDirective],
})
export class AppComponent {
    private authService = inject(AuthService);

    isAdmin = this.authService.activePermission === 'admin';

}
<main>
  <app-learning-resources />

  <hr />

  <app-auth />

  <!-- <p *ngIf="isAdmin()">Only Admins should see that</p> -->
  <p appAuth="admin">Only admins should see this!</p>
</main>
