import { Component, DestroyRef, OnInit, inject, signal } from '@angular/core';

import { Place } from '../place.model';
import { PlacesComponent } from '../places.component';
import { PlacesContainerComponent } from '../places-container/places-container.component';
import { CommonModule } from '@angular/common';
import {HttpClient} from '@angular/common/http'
import { map } from 'rxjs';

@Component({
  selector: 'app-available-places',
  standalone: true,
  templateUrl: './available-places.component.html',
  styleUrls: ['./available-places.component.css'],
  imports: [PlacesComponent, PlacesContainerComponent,CommonModule],
})
export class AvailablePlacesComponent implements OnInit {
    places = signal<Place[] | undefined>(undefined);
    isFetching=signal(false);
    private httpClient = inject(HttpClient);
    private destroyRef = inject(DestroyRef);

    ngOnInit(): void {
        this.isFetching.set(true);
        const subscription = this.httpClient.get<{places:Place[]}>('http://localhost:3000/places',{
            observe:'response'
        }).pipe(
            map((resData)=>resData.body?.places)
            ).subscribe({next:(response)=>{
                this.places.set(response);
        },complete:()=>{
            this.isFetching.set(false);
        },error:(error)=>{
            console.log("error occured while fetching places are: ",error);
        }});

        this.destroyRef.onDestroy(()=>{
            subscription.unsubscribe();
        })
    }
}
<app-places-container title="Available Places">
    <p class="fallback-text">
        Fetching available Places
    </p>
    <app-places *ngIf="places(); else fallback"  [places]="places()!" />
    <ng-template #fallback>
        <p class="fallback-text">Unfortunately, no places could be found.</p>
    </ng-template>

</app-places-container>
