import { Component, DestroyRef, OnInit, inject, signal } from '@angular/core';

import { PlacesContainerComponent } from '../places-container/places-container.component';
import { PlacesComponent } from '../places.component';
import { Place } from '../place.model';
import { HttpClient } from '@angular/common/http';
import { catchError, map, throwError } from 'rxjs';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-user-places',
  standalone: true,
  templateUrl: './user-places.component.html',
  styleUrls: ['./user-places.component.css'],
  imports: [PlacesContainerComponent, PlacesComponent,CommonModule],
})
export class UserPlacesComponent implements OnInit{
    places = signal<Place[] | undefined>(undefined);
    isFetching=signal(false);
    error=signal('');
    private httpClient = inject(HttpClient);
    private destroyRef = inject(DestroyRef);

    ngOnInit(): void {
        this.isFetching.set(true);
        const subscription = this.httpClient.get<{places:Place[]}>('http://localhost:3000/user-places',{
            observe:'response'
        }).pipe(
            map((resData)=>{
                console.log("res data is: ",resData);
               return resData.body?.places;
            }),
            catchError((error)=>{
                console.log("catche error is: ",error)
                return throwError(()=>{
                    return new Error('Something went wrong while fetching your favourite places. Please try again later')
                })
            })
            ).subscribe({next:(response)=>{
                this.places.set(response);
        },complete:()=>{
            this.isFetching.set(false);
        },error:(error:Error)=>{
            console.log("error occured while fetching places are: ",error);
            this.error.set(error.message)
        }});

        this.destroyRef.onDestroy(()=>{
            subscription.unsubscribe();
        })
    }

}
<app-places-container title="Your Favorite Places">
    <p *ngIf="isFetching() && !error()" class="fallback-text">
        Fetching favourite Places...
    </p>
    <p *ngIf="error()" class="fallback-text">
        {{error()}}   
    </p>
    <app-places *ngIf="places()"  [places]="places()!" />
    <p *ngIf="places()?.length===0" class="fallback-text">Unfortunately, no favourite places could be found for the existing user.</p>
</app-places-container>
