import { CommonModule } from '@angular/common';
import { Component, DestroyRef, ViewChild, afterNextRender, inject } from '@angular/core';
import { FormsModule, NgForm } from '@angular/forms';

@Component({
  selector: 'app-login',
  standalone: true,
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
  imports:[FormsModule,CommonModule]
})
export class LoginComponent {
    @ViewChild('form') form!:NgForm;
    private destroyRef = inject(DestroyRef);

    constructor(){
        //similar to use-effect in react

        afterNextRender(()=>{
            const subscription = this.form.valueChanges?.subscribe({next:(value)=>{
                window.localStorage.setItem('saved-login-form',JSON.stringify({email:value.email,password:value.password}))     
            }});

            this.destroyRef.onDestroy(()=>{
                subscription?.unsubscribe()
            })
        })
    }

    onSubmit(formData:NgForm){
        if(!formData.form.valid){
            return;
        }
        const enteredEmail =  formData.form.value.email;
        const enteredPassword = formData.form.value.password;

        formData.form.reset();
    }   
}
<form #form="ngForm" (ngSubmit)="onSubmit(form)">
    <h2>Login</h2>

    <div class="control-row">
        <div class="control no-margin">
        <label for="email">Email</label>
        <input id="email" type="email" name="email" ngModel required email #email="ngModel"/> <!--NgModel registers the input element with angular. Required is a directive to check if the field is not empty. Required field is used since ngModel allows angular to take control of the field -->
        </div>

        <div class="control no-margin">
        <label for="password">Password</label>
        <input id="password" type="password" name="password" ngModel required minlength="6"
        #password="ngModel"/>
        </div>

        <button class="button">Login</button>
    </div>

    <p class="control-error" *ngif="email.touched && email.dirty && email.invalid">
        Invalid Email address entered.
    </p>

    <p class="control-error" *ngif="password.touched && password.dirty && email.invalid">
        Invalid Email address entered.
    </p>
</form>
