import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { FormsModule, NgForm } from '@angular/forms';

@Component({
  selector: 'app-login',
  standalone: true,
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
  imports:[FormsModule,CommonModule]
})
export class LoginComponent {
    onSubmit(formData:NgForm){
        if(!formData.form.valid){
            return;
        }
        const enteredEmail =  formData.form.value.email;
        const enteredPassword = formData.form.value.password;
    }   
}
<form #form="ngForm" (ngSubmit)="onSubmit(form)">
    <h2>Login</h2>

    <div class="control-row">
        <div class="control no-margin">
        <label for="email">Email</label>
        <input id="email" type="email" name="email" ngModel required email/> <!--NgModel registers the input element with angular. Required is a directive to check if the field is not empty. Required field is used since ngModel allows angular to take control of the field -->
        </div>

        <div class="control no-margin">
        <label for="password">Password</label>
        <input id="password" type="password" name="password" ngModel required minlength="6"/>
        </div>

        <button class="button">Login</button>
    </div>

    <p *ngIf="form.form.invalid && form.form.controls['email'].touched && form.form.controls['password'].touched" class="control-error">
        Invalid Values detected. Please check your input
    </p>
</form>
