import { Injectable, signal } from "@angular/core";

@Injectable({
    providedIn:'root'
})
export class MessagesService{
    private messages:string[] = [];
    get allMessages(){
        return [...this.messages]
    } 

    addMessage(message:string){
        this.messages=[...this.messages,message];
    }
}
