import { Component, DestroyRef, OnInit, effect, inject, signal } from '@angular/core';
import {interval, map} from 'rxjs';

@Component({
  selector: 'app-root',
  standalone: true,
  templateUrl: './app.component.html'
})
export class AppComponent implements OnInit{
    clickCount = signal(0);
    interval = signal(0);
    private destroyRef = inject(DestroyRef);

    constructor(){
        //this function gets called whenever any signal value changes
        effect(()=>{
            console.log(`Clicked button ${this.clickCount()} times`)
        })
    }

    ngOnInit(): void {
        setInterval(()=>{
            this.interval.update(prevIntervalNumber=>prevIntervalNumber+1);
        },1000)
        // const subscription = interval(1000).pipe(map((val:number)=>val*2)).subscribe({
        //     next:(val)=>{
        //         console.log(val);
        //     }
        // }); 

        // //this function gets called when the component is destroyed
        // this.destroyRef.onDestroy(()=>{
        //     subscription.unsubscribe();
        // })   
    }

    onClick(){
        this.clickCount.update(prevCount=>prevCount+1);
    }
}
<img src="rxjs-logo.png" alt="The RxJS logo" />
<h1>Understanding RxJS & Observables</h1>
<p>Working with "Streams of Data".</p>

<p>
  <button (click)="onClick()">Click</button>
</p>

<p>
    {{clickCount()}}
</p>
