import { Component, DestroyRef, Input, OnChanges, OnInit, SimpleChanges, inject } from '@angular/core';

import { TaskComponent } from './task/task.component';
import { Task } from './task/task.model';
import { CommonModule } from '@angular/common';
import { TasksService } from './tasks.service';
import { ActivatedRoute, RouterLink } from '@angular/router';

@Component({
  selector: 'app-tasks',
  standalone: true,
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.css'],
  imports: [TaskComponent,CommonModule,RouterLink],
})
export class TasksComponent implements OnChanges,OnInit{

    @Input() userId:string='';
    // @Input() order?:"asc"|"desc";
    order?:'asc'|'desc';
    userTasks: Task[] = [];
    private tasksService = inject(TasksService);
    private destroyRef=inject(DestroyRef);

    private activatedRoute = inject(ActivatedRoute);

    ngOnInit(): void {
        this.activatedRoute.queryParams.subscribe({next:params=>{
            this.order = params['order']
        }})
    }

    ngOnChanges(changes: SimpleChanges): void {
        this.userTasks = this.tasksService.allTasks().filter(task => task.userId === this.userId);
    }

}
<p>
    <a routerLink="./" [queryParams]="{order:order=== 'asc'?'desc':'asc'}">
        Sort {{order==='asc'?'Descending':'Ascending'}}
    </a>
</p>

<ul *ngIf="userTasks.length>0; else noTasks">
    <li  *ngFor="let task of userTasks; else noTasks">
      <app-task [task]="task" />
    </li>
</ul>

<ng-template #noTasks>
    <p>There are no tasks yet. Start adding some!</p>
  </ng-template>
