import { Component, DestroyRef, OnInit, inject } from '@angular/core';
import {interval, map} from 'rxjs';

@Component({
  selector: 'app-root',
  standalone: true,
  templateUrl: './app.component.html'
})
export class AppComponent implements OnInit{
    private destroyRef = inject(DestroyRef);

    ngOnInit(): void {
        const subscription = interval(1000).pipe(map((val:number)=>val*2)).subscribe({
            next:(val)=>{
                console.log(val);
            }
        }); 

        //this function gets called when the component is destroyed
        this.destroyRef.onDestroy(()=>{
            subscription.unsubscribe();
        })   
    }
}
<img src="rxjs-logo.png" alt="The RxJS logo" />
<h1>Understanding RxJS & Observables</h1>
<p>Working with "Streams of Data".</p>

<p>
  <button (click)="onClick()">Click</button>
</p>
